import React, { useState, useEffect } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

function About() {
  const [aboutData, setAboutData] = useState({ logo: "", description: "" });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchAboutData = async () => {
      try {
        const response = await axios.get("http://localhost:8000/api/about/");
        setAboutData(response.data);
        setLoading(false);
      } catch (err) {
        setError("Не вдалося завантажити інформацію про додаток");
        setLoading(false);
        console.error(
          "Помилка запиту:",
          err.response ? err.response.data : err.message
        );
      }
    };
    fetchAboutData();
  }, []);

  if (loading) {
    return (
      <div className="container mt-5 text-center">
        <h1>Про додаток</h1>
        <p>Завантаження...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mt-5 text-center">
        <h1>Про додаток</h1>
        <div className="alert alert-danger">{error}</div>
      </div>
    );
  }

  return (
    <div className="container mt-5 text-center">
      <h1 className="mb-4">Про додаток</h1>
      <img
        src={aboutData.logo}
        alt="Логотип додатку"
        className="img-fluid mb-4"
        style={{ maxWidth: "200px" }}
      />
      <p className="lead">{aboutData.description}</p>
    </div>
  );
}

export default About;
