import React, { useState, useEffect } from "react";
import api from "../api";
import "bootstrap/dist/css/bootstrap.min.css";

function About() {
  const [aboutData, setAboutData] = useState({
    logo: "",
    description: "",
    features: [],
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchAboutData = async () => {
      try {
        const response = await api.get("api/about/");
        setAboutData(response.data);
        setLoading(false);
      } catch (err) {
        setError("Не вдалося завантажити інформацію про додаток");
        setLoading(false);
        console.error("Помилка завантаження даних:", err);
      }
    };
    fetchAboutData();
  }, []);

  if (loading) {
    return (
      <div className="container mt-5 text-center">
        <h1>Про додаток</h1>
        <p>Завантаження...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mt-5 text-center">
        <h1>Про додаток</h1>
        <div className="alert alert-danger">{error}</div>
      </div>
    );
  }

  return (
    <div className="container mt-5">
      <h1 className="mb-4">Про додаток</h1>
      <div className="row">
        {/* Колонка з логотипом/іконкою */}
        <div className="col-md-3 text-center mb-4 mb-md-0">
          {aboutData.logo && (
            <img
              src={aboutData.logo}
              alt="Логотип додатку"
              className="img-fluid"
              style={{ maxHeight: "150px" }}
              onError={(e) => {
                console.log("Помилка завантаження логотипу:", e.target.src);
                e.target.src = "https://picsum.photos/150"; // Запасне зображення
              }}
            />
          )}
        </div>
        {/* Колонка з текстом */}
        <div className="col-md-9">
          <p className="lead mb-4">
            <strong>Мої справи</strong> — ваш надійний помічник у плануванні та
            організації щоденних завдань!
          </p>
          <p>
            Ласкаво просимо до нашого додатку! <strong>Мої справи</strong>{" "}
            створений для того, щоб допомогти вам ефективно керувати своїм часом
            і завданнями. Ви можете легко створювати, редагувати та відмічати
            завершені справи, а також переглядати їх у зручному списку. Ми
            зробили інтерфейс простим і інтуїтивно зрозумілим, щоб ви могли
            зосередитися на своїх цілях, а не на складних налаштуваннях.
          </p>
          <p>{aboutData.description}</p>
          {aboutData.features && aboutData.features.length > 0 && (
            <>
              <h3 className="mt-4">Основні можливості:</h3>
              <ul className="list-group list-group-flush mb-4">
                {aboutData.features.map((feature, index) => (
                  <li key={index} className="list-group-item">
                    {feature}
                  </li>
                ))}
              </ul>
            </>
          )}
          <p>
            <strong>Мої справи</strong> — це ідеальний інструмент для тих, хто
            хоче тримати все під контролем. Незалежно від того, чи плануєте ви
            робочий день, чи складаєте список покупок, ми допоможемо вам
            залишатися організованими.
          </p>
          <p className="mt-4">
            Приєднуйтесь до нас і почніть планувати своє життя вже сьогодні!
          </p>
        </div>
      </div>
    </div>
  );
}

export default About;
