import React, { useState, useEffect } from "react";
import api from "../api";
import "bootstrap/dist/css/bootstrap.min.css";

function Tasks() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState({
    title: "",
    description: "",
    completed: false,
  });
  const [error, setError] = useState("");

  // Завантаження задач
  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await api.get("api/tasks/");
        setTasks(response.data);
      } catch (err) {
        console.error("Не вдалося завантажити задачі:", err);
        setError("Помилка завантаження задач");
      }
    };
    fetchTasks();
  }, []);

  // Обробка зміни полів
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewTask({ ...newTask, [name]: value });
  };

  // Додавання задачі
  const handleAddTask = async (e) => {
    e.preventDefault();

    // Валідація: перевіряємо, чи заповнені поля
    if (!newTask.title.trim()) {
      setError("Заголовок не може бути порожнім");
      return;
    }
    if (!newTask.description.trim()) {
      setError("Опис не може бути порожнім");
      return;
    }

    try {
      setError(""); // Очищаємо попередні помилки
      const response = await api.post("api/tasks/", newTask);
      setTasks([...tasks, response.data]); // Додаємо нову задачу до списку
      setNewTask({ title: "", description: "", completed: false }); // Очищаємо форму
    } catch (err) {
      console.error("Не вдалося додати задачу:", err);
      setError(
        "Помилка додавання задачі: " +
          (err.response?.data?.title || err.message)
      );
      // Не очищаємо tasks, щоб існуючі задачі не зникали
    }
  };
  
  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Мої справи</h1>
      <div className="mb-4">
        <div className="input-group mb-2">
          <input
            type="text"
            className="form-control"
            placeholder="Заголовок"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
        </div>
        <div className="input-group mb-2">
          <textarea
            className="form-control"
            placeholder="Опис"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </div>
      </div>
      <div className="mb-4 d-flex justify-content-end">
        <button className="btn btn-primary btn-sm" onClick={handleAddTask}>
          Додати
        </button>
      </div>
      <ul className="list-group">
        {sortedTasks.map((task) => (
          <li key={task.id} className="list-group-item position-relative">
            <div className="d-flex justify-content-between align-items-center mb-2">
              <div className="d-flex align-items-center">
                <input
                  type="checkbox"
                  checked={task.completed}
                  onChange={() => handleToggleComplete(task.id, task.completed)}
                  className="me-2"
                />
                <strong>{task.title}</strong>
              </div>
              <div className="d-flex">
                <button className="btn btn-outline-primary btn-sm me-2">
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    width="16"
                    height="16"
                    fill="currentColor"
                    viewBox="0 0 16 16"
                  >
                    <path d="M12.146.146a.5.5 0 0 1 .708 0l3 3a.5.5 0 0 1 0 .708l-10 10a.5.5 0 0 1-.168.11l-5 2a.5.5 0 0 1-.65-.65l2-5a.5.5 0 0 1 .11-.168l10-10zM11.207 2.5 13.5 4.793 14.793 3.5 12.5 1.207 11.207 2.5zm1.586 3L10.5 3.207 4 9.707V10h.5a.5.5 0 0 1 .5.5v.5h.5a.5.5 0 0 1 .5.5v.5h.293l6.5-6.5zm-9.761 5.175-.106.106-1.528 3.821 3.821-1.528.106-.106A.5.5 0 0 1 5 12.5V12h-.5a.5.5 0 0 1-.5-.5V11h-.5a.5.5 0 0 1-.468-.325z" />
                  </svg>
                </button>
                <button
                  className="btn btn-outline-danger btn-sm"
                  onClick={() => handleDeleteTask(task.id)}
                >
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    width="16"
                    height="16"
                    fill="currentColor"
                    viewBox="0 0 16 16"
                  >
                    <path d="M5.5 5.5A.5.5 0 0 1 6 5v6a.5.5 0 0 1-1 0V5a.5.5 0 0 1 .5-.5zm2.5 0a.5.5 0 0 1 .5.5v6a.5.5 0 0 1-1 0V5a.5.5 0 0 1 .5-.5zm3 .5a.5.5 0 0 0-1 0v6a.5.5 0 0 0 1 0V5z" />
                    <path
                      fillRule="evenodd"
                      d="M14.5 3a1 1 0 0 1-1 1H13v9a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V4h-.5a1 1 0 0 1-1-1V2a1 1 0 0 1 1-1H6a1 1 0 0 1 1-1h2a1 1 0 0 1 1 1h3.5a1 1 0 0 1 1 1v1zM4.118 4 4 4.059V13a1 1 0 0 0 1 1h6a1 1 0 0 0 1-1V4.059L11.882 4H4.118zM2.5 3V2h11v1h-11z"
                    />
                  </svg>
                </button>
              </div>
            </div>
            <p className="mb-0 ms-4">{task.description}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Tasks;
