import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import LoginModal from "./components/LoginModal"; 

function App() {
  const [showLogin, setShowLogin] = useState(false); // Стан для модального вікна

  return (
    <div className="d-flex flex-column min-vh-100 justify-content-center align-items-center bg-light">
      <h1 className="display-4 text-center mb-3">Плануй свої справи легко!</h1>
      <p className="lead text-center mb-4">
        Організуй свій день із нашим додатком.
      </p>
      <button
        className="btn btn-primary btn-lg"
        onClick={() => setShowLogin(true)}
      >
        Старт
      </button>

      {/* Модальне вікно "Вхід" */}
      {showLogin && <LoginModal onClose={() => setShowLogin(false)} />}
    </div>
  );
}

export default App;
