import React, { useState, useEffect } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

function Profile() {
  const [profile, setProfile] = useState({
    username: "",
    email: "",
    gender: "",
    birth_date: "",
    avatar: "",
  });
  const [editMode, setEditMode] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // Отримання даних профілю
  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem("accessToken");
        if (!token) throw new Error("Токен відсутній");

        const response = await axios.get("http://localhost:8000/api/profile/", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setProfile(response.data);
        setLoading(false);
      } catch (err) {
        setError("Не вдалося завантажити профіль");
        setLoading(false);
        console.error(
          "Помилка запиту:",
          err.response ? err.response.data : err.message
        );
      }
    };
    fetchProfile();
  }, []);

  // Обробка зміни даних у формі
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfile({ ...profile, [name]: value });
  };

  // Збереження змін
  const handleSave = async () => {
    try {
      const token = localStorage.getItem("accessToken");
      await axios.put("http://localhost:8000/api/profile/", profile, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setEditMode(false);
      alert("Профіль успішно оновлено");
    } catch (err) {
      setError("Не вдалося оновити профіль");
      console.error(
        "Помилка збереження:",
        err.response ? err.response.data : err.message
      );
    }
  };

  if (loading) {
    return (
      <div className="container mt-5 text-center">
        <h1>Мій профіль</h1>
        <p>Завантаження...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mt-5 text-center">
        <h1>Мій профіль</h1>
        <div className="alert alert-danger">{error}</div>
      </div>
    );
  }

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Мій профіль</h1>
      {editMode ? (
        <div className="card p-4">
          <div className="mb-3">
            <label htmlFor="username" className="form-label">
              Ім’я користувача
            </label>
            <input
              type="text"
              className="form-control"
              id="username"
              name="username"
              value={profile.username}
              onChange={handleInputChange}
            />
          </div>
          <div className="mb-3">
            <label htmlFor="email" className="form-label">
              Email
            </label>
            <input
              type="email"
              className="form-control"
              id="email"
              name="email"
              value={profile.email}
              onChange={handleInputChange}
            />
          </div>
          <div className="mb-3">
            <label htmlFor="gender" className="form-label">
              Стать
            </label>
            <select
              className="form-select"
              id="gender"
              name="gender"
              value={profile.gender}
              onChange={handleInputChange}
            >
              <option value="">Оберіть стать</option>
              <option value="male">Чоловік</option>
              <option value="female">Жінка</option>
              <option value="other">Інше</option>
            </select>
          </div>
          <div className="mb-3">
            <label htmlFor="birth_date" className="form-label">
              Дата народження
            </label>
            <input
              type="date"
              className="form-control"
              id="birth_date"
              name="birth_date"
              value={profile.birth_date}
              onChange={handleInputChange}
            />
          </div>
          {/* Поки опускаємо avatar, якщо потрібен — додамо пізніше */}
          <button className="btn btn-primary" onClick={handleSave}>
            Зберегти
          </button>
          <button
            className="btn btn-secondary ms-2"
            onClick={() => setEditMode(false)}
          >
            Скасувати
          </button>
        </div>
      ) : (
        <div className="card p-4">
          <p>
            <strong>Ім’я користувача:</strong> {profile.username}
          </p>
          <p>
            <strong>Email:</strong> {profile.email}
          </p>
          <p>
            <strong>Стать:</strong>{" "}
            {profile.gender === "male"
              ? "Чоловік"
              : profile.gender === "female"
              ? "Жінка"
              : "Інше"}
          </p>
          <p>
            <strong>Дата народження:</strong> {profile.birth_date}
          </p>
          <button className="btn btn-primary" onClick={() => setEditMode(true)}>
            Редагувати
          </button>
        </div>
      )}
    </div>
  );
}

export default Profile;
