import React, { useState, useEffect } from "react";
import axios from "axios";
import { Button, Form, ListGroup, InputGroup } from "react-bootstrap";
import { Pencil, Trash } from "react-bootstrap-icons";
import "bootstrap/dist/css/bootstrap.min.css";


const API_URL = "http://localhost:8000/api/tasks/"; // Замініть на ваш API

const Tasks = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState("");
  const [editingTask, setEditingTask] = useState(null);
  const [editText, setEditText] = useState("");

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const response = await axios.get(API_URL);
      setTasks(response.data);
    } catch (error) {
      console.error("Error fetching tasks:", error);
    }
  };

  const addTask = async () => {
    if (!newTask.trim()) return;
    try {
      const response = await axios.post(API_URL, {
        title: newTask,
        completed: false,
      });
      setTasks([...tasks, response.data]);
      setNewTask("");
    } catch (error) {
      console.error("Error adding task:", error);
    }
  };

  const deleteTask = async (id) => {
    try {
      await axios.delete(`${API_URL}${id}/`);
      setTasks(tasks.filter((task) => task.id !== id));
    } catch (error) {
      console.error("Error deleting task:", error);
    }
  };

  const toggleTask = async (id, completed) => {
    try {
      await axios.patch(`${API_URL}${id}/`, { completed: !completed });
      setTasks(
        tasks.map((task) =>
          task.id === id ? { ...task, completed: !completed } : task
        )
      );
    } catch (error) {
      console.error("Error updating task:", error);
    }
  };

  const startEditing = (task) => {
    setEditingTask(task.id);
    setEditText(task.title);
  };

  const saveEdit = async (id) => {
    try {
      await axios.patch(`${API_URL}${id}/`, { title: editText });
      setTasks(
        tasks.map((task) =>
          task.id === id ? { ...task, title: editText } : task
        )
      );
      setEditingTask(null);
    } catch (error) {
      console.error("Error editing task:", error);
    }
  };

  return (
    <div className="container mt-4">
      <h2 className="text-center">My Tasks</h2>

      <InputGroup className="mb-3">
        <Form.Control
          type="text"
          placeholder="Enter text"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
        />
        <Button variant="success" onClick={addTask}>
          Add
        </Button>
      </InputGroup>

      <ListGroup>
        {tasks.map((task) => (
          <ListGroup.Item key={task.id} className="d-flex align-items-center">
            <Form.Check
              type="checkbox"
              checked={task.completed}
              onChange={() => toggleTask(task.id, task.completed)}
              className="me-2"
            />

            {editingTask === task.id ? (
              <Form.Control
                type="text"
                value={editText}
                onChange={(e) => setEditText(e.target.value)}
                onBlur={() => saveEdit(task.id)}
                autoFocus
              />
            ) : (
              <span
                className={`flex-grow-1 ${
                  task.completed ? "text-decoration-line-through" : ""
                }`}
              >
                {task.title}
              </span>
            )}

            <Button variant="link" onClick={() => startEditing(task)}>
              <Pencil />
            </Button>
            <Button
              variant="link"
              className="text-danger"
              onClick={() => deleteTask(task.id)}
            >
              <Trash />
            </Button>
          </ListGroup.Item>
        ))}
      </ListGroup>
    </div>
  );
};

export default Tasks;
