import React, { useState, useEffect } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

function Profile() {
  const [profile, setProfile] = useState({
    username: "",
    email: "",
    gender: "",
    birth_date: "",
    avatar: "",
  });
  const [editMode, setEditMode] = useState(false);
  const [avatarFile, setAvatarFile] = useState(null); // Для зберігання файлу аватара
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // Отримання даних профілю
  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem("accessToken");
        if (!token) throw new Error("Токен відсутній");

        const response = await axios.get("http://localhost:8000/api/profile/", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setProfile(response.data);
        setLoading(false);
      } catch (err) {
        setError("Не вдалося завантажити профіль");
        setLoading(false);
        console.error(
          "Помилка запиту:",
          err.response ? err.response.data : err.message
        );
      }
    };
    fetchProfile();
  }, []);

  // Обробка зміни текстових полів
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfile({ ...profile, [name]: value });
  };

  // Обробка зміни файлу аватара
  const handleAvatarChange = (e) => {
    setAvatarFile(e.target.files[0]);
  };

  // Збереження змін
  const handleSave = async () => {
    try {
      const token = localStorage.getItem("accessToken");
      const formData = new FormData();
      formData.append("username", profile.username);
      formData.append("email", profile.email);
      formData.append("gender", profile.gender);
      formData.append("birth_date", profile.birth_date);
      if (avatarFile) {
        formData.append("avatar", avatarFile); // Додаємо файл аватара, якщо є
      }

      const response = await axios.put(
        "http://localhost:8000/api/profile/",
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "multipart/form-data", // Для завантаження файлів
          },
        }
      );
      setProfile(response.data); // Оновлюємо дані профілю
      setAvatarFile(null); // Очищаємо файл після збереження
      setEditMode(false);
      alert("Профіль успішно оновлено");
    } catch (err) {
      setError("Не вдалося оновити профіль");
      console.error(
        "Помилка збереження:",
        err.response ? err.response.data : err.message
      );
    }
  };

  if (loading) {
    return (
      <div className="container mt-5 text-center">
        <h1>Мій профіль</h1>
        <p>Завантаження...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mt-5 text-center">
        <h1>Мій профіль</h1>
        <div className="alert alert-danger">{error}</div>
      </div>
    );
  }

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Мій профіль</h1>
      <div className="row">
        {/* Колонка для аватара */}
        <div className="col-md-4 text-center">
          <img
            src={profile.avatar || "https://via.placeholder.com/150"} // Запасне зображення, якщо avatar відсутній
            alt="Аватар користувача"
            className="img-fluid rounded-circle mb-3"
            style={{ maxWidth: "150px" }}
          />
          {editMode && (
            <div className="mb-3">
              <input
                type="file"
                className="form-control"
                accept="image/*"
                onChange={handleAvatarChange}
              />
            </div>
          )}
        </div>

        {/* Колонка для інформації */}
        <div className="col-md-8">
          {editMode ? (
            <div className="card p-4">
              <div className="mb-3">
                <label htmlFor="username" className="form-label">
                  Ім’я користувача
                </label>
                <input
                  type="text"
                  className="form-control"
                  id="username"
                  name="username"
                  value={profile.username}
                  onChange={handleInputChange}
                />
              </div>
              <div className="mb-3">
                <label htmlFor="email" className="form-label">
                  Email
                </label>
                <input
                  type="email"
                  className="form-control"
                  id="email"
                  name="email"
                  value={profile.email}
                  onChange={handleInputChange}
                />
              </div>
              <div className="mb-3">
                <label htmlFor="gender" className="form-label">
                  Стать
                </label>
                <select
                  className="form-select"
                  id="gender"
                  name="gender"
                  value={profile.gender}
                  onChange={handleInputChange}
                >
                  <option value="">Оберіть стать</option>
                  <option value="male">Чоловік</option>
                  <option value="female">Жінка</option>
                  <option value="other">Інше</option>
                </select>
              </div>
              <div className="mb-3">
                <label htmlFor="birth_date" className="form-label">
                  Дата народження
                </label>
                <input
                  type="date"
                  className="form-control"
                  id="birth_date"
                  name="birth_date"
                  value={profile.birth_date}
                  onChange={handleInputChange}
                />
              </div>
              <button className="btn btn-primary" onClick={handleSave}>
                Зберегти
              </button>
              <button
                className="btn btn-secondary ms-2"
                onClick={() => setEditMode(false)}
              >
                Скасувати
              </button>
            </div>
          ) : (
            <div className="card p-4">
              <p>
                <strong>Ім’я користувача:</strong> {profile.username}
              </p>
              <p>
                <strong>Email:</strong> {profile.email}
              </p>
              <p>
                <strong>Стать:</strong>{" "}
                {profile.gender === "male"
                  ? "Чоловік"
                  : profile.gender === "female"
                  ? "Жінка"
                  : "Інше"}
              </p>
              <p>
                <strong>Дата народження:</strong> {profile.birth_date}
              </p>
              <button
                className="btn btn-primary"
                onClick={() => setEditMode(true)}
              >
                Редагувати
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default Profile;
