import React, { useState } from "react";
import {
  BrowserRouter as Router,
  Route,
  Routes,
  Navigate,
  useNavigate,
} from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import LoginModal from "./components/LoginModal";
import RegisterModal from "./components/RegisterModal";
import Tasks from "./components/Tasks";
import Header from "./components/Header"; // Імпортуємо Header

function App() {
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(
    !!localStorage.getItem("accessToken")
  );
  const navigate = useNavigate();

  const handleLoginSuccess = () => {
    setIsAuthenticated(true);
    setShowLogin(false);
    navigate("/tas");
  };

  const handleSwitchToRegister = () => {
    setShowLogin(false);
    setShowRegister(true);
  };

  const handleSwitchToLogin = () => {
    setShowRegister(false);
    setShowLogin(true);
  };

  return (
    <div>
      {isAuthenticated && <Header />} {/* Використовуємо імпортований Header */}
      <Routes>
        <Route
          path="/"
          element={
            isAuthenticated ? (
              <Navigate to="/tasks" />
            ) : (
              <div className="d-flex flex-column min-vh-100 justify-content-center align-items-center bg-light">
                <h1 className="display-4 text-center mb-3">
                  Плануй свої справи легко!
                </h1>
                <p className="lead text-center mb-4">
                  Організуй свій день із нашим додатком.
                </p>
                <button
                  className="btn btn-primary btn-lg"
                  onClick={() => setShowLogin(true)}
                >
                  Старт
                </button>
                {showLogin && (
                  <LoginModal
                    onClose={() => setShowLogin(false)}
                    onLoginSuccess={handleLoginSuccess}
                    onSwitchToRegister={handleSwitchToRegister}
                  />
                )}
                {showRegister && (
                  <RegisterModal
                    onClose={() => setShowRegister(false)}
                    onSwitchToLogin={handleSwitchToLogin}
                  />
                )}
              </div>
            )
          }
        />
        <Route
          path="/tasks"
          element={isAuthenticated ? <Tasks /> : <Navigate to="/" />}
        />
      </Routes>
    </div>
  );
}

export default function AppWrapper() {
  return (
    <Router>
      <App />
    </Router>
  );
}
