import React, { useState, useEffect } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

function Tasks() {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // Отримання завдань
  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get("http://localhost:8000/api/tasks/", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setTasks(response.data);
        setLoading(false);
      } catch (err) {
        setError("Не вдалося завантажити завдання");
        setLoading(false);
        console.error(err);
      }
    };
    fetchTasks();
  }, []);

  // Додавання нового завдання
  const handleAddTask = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.post(
        "http://localhost:8000/api/tasks/",
        { title, description, completed: false },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setTasks([...tasks, response.data]);
      setTitle("");
      setDescription("");
    } catch (err) {
      setError("Не вдалося додати завдання");
      console.error(err);
    }
  };

  // Оновлення статусу завдання
  const handleToggleComplete = async (taskId, currentStatus) => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.put(
        `http://localhost:8000/api/tasks/${taskId}/`,
        { completed: !currentStatus },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setTasks(
        tasks.map((task) =>
          task.id === taskId
            ? { ...task, completed: response.data.completed }
            : task
        )
      );
    } catch (err) {
      console.error("Помилка оновлення статусу:", err);
    }
  };

  // Видалення завдання
  const handleDeleteTask = async (taskId) => {
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`http://localhost:8000/api/tasks/${taskId}/`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTasks(tasks.filter((task) => task.id !== taskId));
    } catch (err) {
      console.error("Помилка видалення:", err);
    }
  };

  if (loading) {
    return (
      <div className="container mt-5 text-center">
        <h1>Мої справи</h1>
        <p>Завантаження...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mt-5 text-center">
        <h1>Мої справи</h1>
        <div className="alert alert-danger">{error}</div>
      </div>
    );
  }

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Мої справи</h1>
      {/* Форма для додавання завдання */}
      <div className="mb-4">
        <div className="input-group mb-2">
          <input
            type="text"
            className="form-control"
            placeholder="Заголовок"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
        </div>
        <div className="input-group mb-2">
          <textarea
            className="form-control"
            placeholder="Опис"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
        </div>
        <button className="btn btn-primary w-100" onClick={handleAddTask}>
          Додати
        </button>
      </div>
      {/* Список завдань */}
      <ul className="list-group">
        {tasks.map((task) => (
          <li
            key={task.id}
            className="list-group-item d-flex justify-content-between align-items-start"
          >
            <div>
              <input
                type="checkbox"
                checked={task.completed}
                onChange={() => handleToggleComplete(task.id, task.completed)}
                className="me-2"
              />
              <strong>{task.title}</strong>
              <p className="mb-0">{task.description}</p>
            </div>
            <div>
              <button className="btn btn-outline-primary btn-sm me-2">
                Редагувати
              </button>
              <button
                className="btn btn-outline-danger btn-sm"
                onClick={() => handleDeleteTask(task.id)}
              >
                Видалити
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Tasks;
