import React, { useState, useEffect } from 'react';
import api from '../api';
import 'bootstrap/dist/css/bootstrap.min.css';

function Tasks() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState({ title: '', description: '', completed: false });
  const [error, setError] = useState('');

  // Завантаження задач
  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const response = await api.get('api/tasks/');
        setTasks(response.data);
      } catch (err) {
        console.error('Не вдалося завантажити задачі:', err);
        setError('Помилка завантаження задач');
      }
    };
    fetchTasks();
  }, []);

  // Обробка зміни полів
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewTask({ ...newTask, [name]: value });
  };

  // Додавання задачі
  const handleAddTask = async (e) => {
    e.preventDefault();

    // Валідація: перевіряємо, чи заповнені поля
    if (!newTask.title.trim()) {
      setError('Заголовок не може бути порожнім');
      return;
    }
    if (!newTask.description.trim()) {
      setError('Опис не може бути порожнім');
      return;
    }

    try {
      setError(''); // Очищаємо попередні помилки
      const response = await api.post('api/tasks/', newTask);
      setTasks([...tasks, response.data]); // Додаємо нову задачу до списку
      setNewTask({ title: '', description: '', completed: false }); // Очищаємо форму
    } catch (err) {
      console.error('Не вдалося додати задачу:', err);
      setError('Помилка додавання задачі: ' + (err.response?.data?.title || err.message));
      // Не очищаємо tasks, щоб існуючі задачі не зникали
    }
  };

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">Мої завдання</h1>
      {error && <div className="alert alert-danger">{error}</div>}

      {/* Форма для додавання задачі */}
      <form onSubmit={handleAddTask} className="mb-4">
        <div className="mb-3">
          <label htmlFor="title" className="form-label">Заголовок</label>
          <input
            type="text"
            className="form-control"
            id="title"
            name="title"
            value={newTask.title}
            onChange={handleInputChange}
            placeholder="Введіть заголовок"
          />
        </div>
        <div className="mb-3">
          <label htmlFor="description" className="form-label">Опис</label>
          <textarea
            className="form-control"
            id="description"
            name="description"
            value={newTask.description}
            onChange={handleInputChange}
            placeholder="Введіть опис"
          />
        </div>
        <button type="submit" className="btn btn-primary">Додати</button>
      </form>

      {/* Список задач */}
      <div className="row">
        {tasks.length === 0 ? (
          <p className="text-center">Немає задач</p>
        ) : (
          tasks.map(task => (
            <div key={task.id} className="col-md-4 mb-3">
              <div className="card">
                <div className="card-body">
                  <h5 className="card-title">{task.title}</h5>
                  <p className="card-text">{task.description}</p>
                  <p className="card-text">
                    <small className="text-muted">
                      {task.completed ? 'Виконано' : 'Не виконано'}
                    </small>
                  </p>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default Tasks;
